import React, { Component,forwardRef } from 'react';
import {View, Text, StyleSheet ,Image, TouchableOpacity,Linking } from 'react-native';

import DatePicker from "react-datepicker";
import "react-datepicker/dist/react-datepicker.css";

export default class Verification extends Component {
  state = {
    date: ''
  }
  render() {
    const CustomDatePicker = forwardRef(
      ({ value, onClick }, ref) => (
        <View style={{width: '100%', height: '56px', backgroundColor: '#fff',borderRadius: 5,borderColor: '#BBB',display: 'flex',justifyContent: 'center'}} onClick={onClick} ref={ref}>
          <Text style={{paddingLeft: 20,fontSize: 15,color: '#6D6D6D'}}>{value ? value: 'DD/MM/YY'}</Text>
        </View>
      ),
    );
    return (
      <View>
        <Text style={styles.h4}>Type your date of birth and election code to vote</Text>
        <View style={{fontSize: 12}}>
          <Text>Date of birth</Text>
          <DatePicker
            showWeekNumbers
            selected={this.state.date}
            onChange={date => this.setState({date: date})}
            customInput={<CustomDatePicker />}
          />
          <Text>Type your date of birth as dd/mm/yy, eg .01/01/90</Text>
        </View>
      </View>
    )
  }
}

const  styles = StyleSheet.create({
  h4: {
    fontSize: 20,
    fontWeight: 700,
    color: '#fff',
    marginBottom: 28
  }
})
