import React, { Component } from 'react';
import {ImageBackground, View, Text, StyleSheet, Platform , Image} from 'react-native';
const image = { uri: "/src/assets/images/banner.jpg"};

export default class Home extends Component {
  render() {
    return (
      <View style={styles.container}>
        <ImageBackground style={styles.banner} source={image}>
          {Platform.OS === 'web' ? <Text>I am a Text</Text> : <Text>This is for Mobile</Text>}
        </ImageBackground>
      </View>
    )
  }
}
const styles = StyleSheet.create({
  container: {
    flex: 1,
    alignItems: 'center',
    justifyContent: 'center',
    height: '100%',
    width: '100%',
  }, banner: {
    flex: 1,
    alignItems: 'center',
    justifyContent: 'center',
    height: '100%',
    width: '100%',
    resizeMode: "cover",
    backgroundColor: '#1D9FE4',
  }
});
