import React, { Component,forwardRef } from 'react';
import {View, Text, StyleSheet ,Image, TouchableOpacity,TextInput  } from 'react-native';

import DatePicker from "react-datepicker";
import "react-datepicker/dist/react-datepicker.css";

export default class Verification extends Component {
  state = {
    date: ''
  }
  render() {
    const CustomDatePicker = forwardRef(
      ({ value, onClick }, ref) => (
        <View style={{width: '100%', height: '56px', backgroundColor: '#fff',borderRadius: 5,borderColor: '#BBB',display: 'flex',justifyContent: 'center',cursor: 'pointer'}} onClick={onClick} ref={ref}>
          <Text style={{paddingLeft: 20,fontSize: 15,color: '#6D6D6D'}}>{value ? value: 'DD/MM/YY'}</Text>
        </View>
      ),
    );
    return (
      <View>
        <Text style={styles.h4}>Type your date of birth and election code to vote</Text>
        <View style={styles.row,{position: 'relative', zIndex: 99}}>
          <Text style={styles.label}>Date of birth</Text>
          <DatePicker
            showWeekNumbers
            selected={this.state.date}
            onChange={date => this.setState({date: date})}
            customInput={<CustomDatePicker />}
          />
          <Text style={styles.info}>Type your date of birth as dd/mm/yy, eg .01/01/90</Text>
        </View>
        <View style={styles.row}>
          <Text style={styles.label}>Election code</Text>
          <TextInput
            placeholder="ELECTION CODE"
            style={{ height: 56, borderColor: '#ddd', borderWidth: 1,borderRadius: 5, backgroundColor: '#fff',paddingLeft: 20,paddingRight: 20 }}
          />
          <Text style={styles.info}>Type your election code sent to you by message or email</Text>
        </View>
        <View style={{display: 'flex', alignItems: 'flex-end'}}>
          <TouchableOpacity onPress={this.props.onPress(2)} style={styles.btn}>
            <Text style={{fontSize: '15px', color: '#fff', marginRight: 10,fontWeight: '700'}}>Go To Ballot</Text>
            <Image style={{width: '16px', height: '10px', resizeMode: 'cover'}} source={require('../../assets/img/arrow-right.svg')} />
          </TouchableOpacity>
        </View>
      </View>
    )
  }
}

const  styles = StyleSheet.create({
  h4: {
    fontSize: 20,
    fontWeight: 700,
    color: '#fff',
    marginBottom: 28
  },
  row: {
    marginBottom: 24
  },
  label: {
    fontSize: 15,
    fontWeight: 700,
    color: '#fff',
    marginBottom: 6
  },
  info: {
    fontSize: 11,
    color: '#fff',
    marginTop: 7
  },
  btn: {
    display: 'flex',
    flexDirection: 'row',
    width: '200px',
    height: '50px',
    alignItems: 'center',
    justifyContent: 'center',
    backgroundColor: 'transparent',
    borderRadius: '4px',
    borderWidth: 1,
    borderColor: '#fff'
  },
})
