import React, { Component, useState  } from 'react';
import {View, Text, StyleSheet ,Image, TouchableOpacity,Linking } from 'react-native';
import Verification from './Verification';
import "react-datepicker/dist/react-datepicker.css";


export default class ViewPoint extends Component {
  constructor(props) {
    super(props);
    this.handlePress = this.handlePress.bind(this)
  }
  state = {
    step: 1
  }
   handlePress = (item) => {
    alert(item);
    this.setState({step: item})
  }
  render() {
    
    return (
      <View style={styles.container}>
        <View style={styles.leftContainer}>
          <Image style={{ resizeMode: 'cover', width: '252px', height: '70px',marginBottom: 24 }}source={require('../../assets/img/logo.svg')} />
          <Text style={styles.heading}>ELECTION OK21</Text>
          <TouchableOpacity>
            <View style={styles.btn}>
              <Text style={{fontSize: '15px', color: '#fff', marginRight: 6,fontWeight: '400'}}>Vote with</Text>
              <Image style={{width: '56px', height: '12px', resizeMode: 'cover'}} source={require('../../assets/img/nem.svg')} />
            </View>
            </TouchableOpacity>
            <Text style={styles.text}>Should you experience any problem voting, please contact out hotline via email?</Text>
            <Text style={styles.text}>Should you experience any problem voting, please contact out hotline via email: <TouchableOpacity style={{color: '#2D85D5',textDecoration: 'underline'}} onPress={() => Linking.openURL('mailto:abc@abc.com')}><Text>abc@abc.com</Text></TouchableOpacity> or via telephone: 4697-3676. Our hotline is open on working days between 8.30 am to 4.00 pm. </Text>
        </View>
        <View style={styles.rightContainer}>
          {this.state.step === 1 && 
            <Verification click={this.handlePress()} />
          }
        </View>
      </View>
    )
  }
}

const styles = StyleSheet.create({
  container: {
    width: '1260px',
    display: 'flex',
    flexDirection: 'row',
    minHeight: 350,
    justifyContent: 'center'
  },
  leftContainer: {
    width: '35%',
    padding: '60px',
    backgroundColor: 'rgba(255,255,255,0.75)',
    height: '100%',
    display: 'flex',
    flexDirection: 'column',
    justifyContent: 'center',
  },
  rightContainer: {
    flex: 1,
    width: '65%',
    backgroundColor: 'rgba(0,117,163,0.75)',
    height: '100%',
    display: 'flex',
    flexDirection: 'column',
    justifyContent: 'center',
    paddingLeft: '55px',
    paddingRight: '55px',
    paddingTop: '80px',
    paddingBottom: '80px',
  },
  heading: {
    textTransform: 'uppercase',
    fontWeight: 700,
    fontSize: '32px',
    color: '#0075A3',
    marginBottom: 15
  },
  btn: {
    display: 'flex',
    flexDirection: 'row',
    width: '200px',
    height: '40px',
    alignItems: 'center',
    justifyContent: 'center',
    backgroundColor: '#1C9DE3',
    borderRadius: '4px'
  },
  text: {
    fontSize: 14,
    color: '#707070',
    marginTop: 18
  }
})
